<?phpnamespace app\api\controller;use think\Request;use think\controller\Rest;use think\Db;header('Access-Control-Allow-Origin:*');class System extends Rest{    public function rest()    {        switch ($this->method){            case 'get':     //查询                $this->Find($id);                break;            case 'post':    //新增                $this->UpFiles();                break;        }    }    /*系统信息*/    public function Find($id)    {        $data = Request::instance()->param();        $find = findone('admin',[],'id,username,title,phone',['id'=>$data['id']]);        if($find){            echo json(200,$find);        }else{            echo json(202,'');        }    }//    /*修改系统信息*/////    public function Edit()//    {////        $data = Request::instance()->param();//        $logo = isset($data['logo']) ? $data['logo'] : "";//        $arr = [//            'title'=>$data['title'],//            'logo'=>$logo//        ];//        $edit = edit('admin',['id'=>$data['user_adminid']],$arr);//        if($edit){//            echo json(200,$data);//        }elseif($edit == null){//            echo json(200,$data);//        }else{//            echo json(202,'');//        }//    }    /*上传*/    public function UpFiles()    {        $data = Request::instance()->param();        $data['id'] = $data['user_adminid'];        unset($data['user_adminid']);        $edit = Db::table('admin')->where('id', $data['id'])->update($data);        if($edit >= 0){            echo json(200,$data);        }else{            echo json(202,'');        }    }}